-- Migración completa de productos desde CSV
-- Fecha: $(date)
-- Descripción: Migra todos los productos del archivo productos_final_pinteya.csv
-- Incluye: Limpieza de datos existentes, adición de campo aikon_id, carga completa

BEGIN;

-- 1. Agregar campo aikon_id a la tabla products si no existe
DO $$ 
BEGIN
    IF NOT EXISTS (SELECT 1 FROM information_schema.columns 
                   WHERE table_name = 'products' AND column_name = 'aikon_id') THEN
        ALTER TABLE products ADD COLUMN aikon_id VARCHAR(50);
        CREATE INDEX IF NOT EXISTS idx_products_aikon_id ON products(aikon_id);
    END IF;
END $$;

-- 2. Agregar campos adicionales del CSV si no existen
DO $$ 
BEGIN
    IF NOT EXISTS (SELECT 1 FROM information_schema.columns 
                   WHERE table_name = 'products' AND column_name = 'color') THEN
        ALTER TABLE products ADD COLUMN color VARCHAR(100);
    END IF;
    
    IF NOT EXISTS (SELECT 1 FROM information_schema.columns 
                   WHERE table_name = 'products' AND column_name = 'medida') THEN
        ALTER TABLE products ADD COLUMN medida VARCHAR(50);
    END IF;
END $$;

-- 3. Limpiar datos existentes (mantener estructura de tablas)
DELETE FROM order_items WHERE product_id IN (SELECT id FROM products);
DELETE FROM cart_items WHERE product_id IN (SELECT id FROM products);
DELETE FROM products;

-- 4. Resetear secuencia de IDs para que coincida con el CSV
SELECT setval('products_id_seq', 40, false);

-- 5. Insertar todos los productos del CSV
